const getProduct = (number = 1) => {
  let rs = [];
  products.forEach((el, index) => {
    if (index <= number) rs.push(el);
  });
  return rs;
};

const getProductById = (slug) => {
  let rs = products.filter((el) => el.slug === slug);
  if (rs.length !== 0) return rs[0];
  else return null;
};

const getProductByCategory = (category) => {
  let rs = products.filter((el) => el.category === category);
  if (rs.length !== 0) return rs;
  else return null;
};

export { getProduct, getProductById, getProductByCategory };
